mapper.select:
SELECT c.customer_name, e.`emp_name`, c.customer_status, c.customer_linkman, c.customer_phone, c.pro_address, s.shop_name, c.create_date FROM customer c INNER JOIN cust_emp_relation cm ON c.id = cm.`customer_id` INNER JOIN employee e ON cm.`emp_id` = e.`id` INNER JOIN shop s ON e.shop_id = s.id
and c.`customer_name` like CONCAT('%',#{condition},'%')OR e.`emp_name` LIKE CONCAT('%',#{condition},'%')OR customer_linkman LIKE CONCAT('%',#{condition},'%') OR c.pro_address LIKE CONCAT('%',#{condition},'%')OR c.customer_phone LIKE CONCAT('%',#{condition},'%')
and c.customer_status = #{customerStatus}

SELECT c.`id`, c.`customer_name`, e.`shop_id`, c.`customer_status`, c.`customer_linkman`, c.`customer_phone` FROM customer c INNER JOIN cust_emp_relation cm ON c.id = cm.`customer_id` INNER JOIN employee e ON cm.`emp_id` = e.`id` INNER JOIN shop s ON e.shop_id = s.id
and c.`customer_name` like CONCAT('%',#{condition},'%') OR customer_linkman LIKE CONCAT('%',#{condition},'%') OR c.customer_phone LIKE CONCAT('%',#{condition},'%')
and c.customer_status = #{customerStatus}
