mapper.select:
select w.`id`, s.`shop_name`, c.`customer_name`, w.`single_state`, w.`take_state`, w.`place_order_date`, e.`emp_name`, e.`emp_phone`, s.`shop_address`, w.`ready_date` from whole_order w inner join shop s on w.`shop_id` = s.`id` inner join customer c on w.`customer_id` = c.`id` inner join employee e on w.`emp_id` = e.`id`
w.place_order_date between #{startDate} and #{endDate}
and w.`single_state` = #{singleState}
and w.`take_state` = #{takeState}
and w.`id` like concat('%',#{condition},'%') or s.`shop_name`like concat('%',#{condition},'%') or c.`customer_name`like concat('%',#{condition},'%') or e.`emp_name`like concat('%',#{condition},'%') or e.`emp_phone`like concat('%',#{condition},'%') or s.`shop_address`like concat('%',#{condition},'%')

mapper.update:
  UPDATE whole_order


        customer_id = #{customerId},


        shop_id = #{shopId},


        emp_id = #{empId},


        ready_date = #{readyDate},


        single_state = #{singleState}


        take_state = #{takeState}


WHERE id = #{id}
